import React from 'react';

const SignIn = () => {
    return (
        <div className="main-container">
            <div className="container">
                <div className="logo-container">
                    <img className="logo-image" src="/images/logo.png" />
                    <img className="logo-name" src="/images/GiftÃœ.png" />
                </div>
                <p className="title">SIGN IN</p>
                <form className="form">
                    <div className="input-container">
                        <span className="label">
                            Email
                            <img className="input-image__mail" src="/images/mail2.png" />
                        </span>
                        <input type="text" className="input"/>
                    </div>
                    <div className="input-container">
                        <span className="label">
                            Password
                            <img className="input-image__key" src="/images/key.png" />
                        </span>
                        <input type="text" className="input"/>
                    </div>
                </form>
            </div>
            <style jsx> {`
                    .main-container {
                        width: 100%;
                        height: 100vh;
                        display: flex;
                        flex-direction: column;
                        align-items: center;
                        justify-content: center;
                    }
                    .container {
                        width: 100%;
                        height: 500px;
                        display: flex;
                        flex-direction: column;
                        align-items: center;
                        justify-content: center;
                        background: green;
                    }
                    .form {
                        display: flex;
                        flex-direction: column;
                        min-height: 200px;
                        justify-content: space-between;
                        align-items: center;
                    }
                    .logo-container {
                        display: flex;
                        flex-direction: column;
                        align-items: center;
                    }
                    .logo-image {
                        height: 100px;
                        width: auto;
                    }
                    .logo-name {
                        height: auto;
                        width: 100px;
                    }
                    .title {
                        line-height: 30px;
                        letter-spacing: 0.14em;
                        font-weight: bold;
                        font-size: 24px;
                        font-family: 'Quicksand', sans-serif;
                        margin: 30px 0;
                    }
                    .label {
                        font-family: 'Quicksand', sans-serif;
                        font-size: 14px;
                        line-height: 17px;
                        letter-spacing: 0.14em;
                        font-feature-settings: 'case'
                        display: flex;
                        align-items: center;
                        width: 100%;
                        height: 30px;
                    }
                    .input-container {
                        display: flex;
                        flex-direction: column;
                        justify-content: space-around;
                    }
                    .input {
                        width: 258px;
                        background: transparent;
                        border: none;
                        border-bottom: 2px solid rgba(135, 211, 184, 0.6);
                        font-family: 'Quicksand', sans-serif;
                        font-size: 14px;
                        line-height: 17px;
                        color: #FFAB07;
                        padding: 10px 0;
                    }
                    .input:focus, textarea:focus, select:focus{
                        outline: none;
                    }
                    .input-image {
                        width: 16px;
                        height: 16px;
                        margin-left: 8px;
                    }
                    .input-image__mail {
                        transform: rotate(25deg);
                    }
                    .input-image__key {

                    }

                `}
            </style>
        </div>

    );
};

export default SignIn;
